function DisplayErrorDialog(displayTitle) {
    $("#errorDialog").dialog({
        resizable: false,
        height: 'auto',
        width: 'auto',
        modal: true,
        title: displayTitle,
        //dialogClass: "noclose",
        closeOnEscape: false,
        buttons: {
            Ok: function () {
                $(this).dialog("close");
            }
        }
    }); //end dialog
}

